import { Hits } from "react-instantsearch";
import './Results.css';


function Hit({ hit }) {
    return (
      <div className="card">
        <img src={hit.coverImage} alt={hit.objectID} />
        <div className="card-content">
          <h2>{hit.title}</h2>
          <p>{hit.author}</p>
          <a href={hit.objectID} className="button">
            Find out more 
            <span className="material-symbols-outlined">
              arrow_right_alt
            </span>
          </a>
        </div>
      </div>
    );
  }


function Results() {
    return (
        <Hits 
            hitComponent={Hit}
            classNames={{
              item: 'responsive',
            }}
        />
    );
    }

export default Results;
