import { Hits } from "react-instantsearch";
import './Results.css';


function Hit({ hit }) {
    return (

        <div className="gallery">
          {hit.coverImage && <img src={hit.coverImage} alt={hit.objectID} className="hit-image" />} 
          <a href={hit.objectID}>{hit.title}</a>  
          <p>{hit.author}</p>
        </div>
    );
  }


function Results() {
    return (
        <Hits 
            hitComponent={Hit}
            classNames={{
              item: 'responsive',
            }}
        />
    );
    }

export default Results;
