import React, { useState, useEffect } from "react";
import { useRange } from 'react-instantsearch';
import { RangeSlider as SpectrumRangeSlider } from '@adobe/react-spectrum';
import "./RangeSlider.css"

export function RangeSlider({title,...props}) {
  const { start, range, canRefine, refine } = useRange(props);
  const { min, max } = range;
  const [value, setValue] = useState({ start: min, end: max });

  const from = Math.max(min, Number.isFinite(start[0]) ? start[0] : min);
  const to = Math.min(max, Number.isFinite(start[1]) ? start[1] : max);

  useEffect(() => {
    setValue({ start: from, end: to });
  }, [from, to]);

  return (
    <div className="customeRefinementList">
      <div className="range">
      {title && <h3>{title}</h3>}

          <SpectrumRangeSlider
            minValue={min}
            maxValue={max}
            value={value}
            onChange={setValue}
            onChangeEnd={({ start, end }) => refine([start, end])}
            isDisabled={!canRefine}
          />

      </div>
      <ul className="range-labels">
        {Array.from({ length: max - min + 1 }, (_, i) => (
          <li key={i} className={value.start === min + i ? 'active selected' : ''}>
          <div className="increment-circles">
            {Array.from({ length: max - min + 1 }, (_, i) => (
              <div key={i} className="circle" style={{ 
                width: '10px', 
                height: '10px', 
                borderRadius: '50%', 
                backgroundColor: 'blue', 
                position: 'absolute', 
                left: `${((min + i - min) / (max - min)) * 100}%`, // Positioning based on the range
                transform: 'translateX(-20%)' // Center the circle
              }} />
            ))}
          </div>
            {min + i}
          </li>
        ))}
      </ul>
    </div>
  );
}
